SET DATEFORMAT dmy;
DROP TABLE UserTable;
CREATE      TABLE       UserTable (
  userID    INTEGER     PRIMARY KEY,
  mail      VARCHAR(255) CHECK (email LIKE '%@hcmut.edu.vn'),
  name      VARCHAR(255),
  DoB       DATE,
  sex       VARCHAR(10)
);

ALTER TABLE User
ADD CONSTRAINT CK_Sex
CHECK (sex IN ('Male', 'Female'));

